<?phpuse app\widgets\DSlideWidgetTeam\DSlideWidgetAsset;use yii\helpers\Html;DSlideWidgetAsset::register($this);$lang_id = Yii::$app->language;if (isset($_REQUEST['lang_id'])){    $lang_id = $_REQUEST['lang_id'];}if ($lang_id == 'ka-GE'){    $font_family = 'bpg-web-001-caps !important';    $font_size = '20px !important';}if ($lang_id == 'en-US'){    $font_family = 'BebasNeueRegular !important';    $font_size = '26px !important';}?><div class="row ds-container-team">    <div class="large-7 seven columns ds-items-container-team blue">        <div class="row ds-items-team" data-slick='<?= $options ?>'>            <?php foreach($items as $item): ?>                <div class="ds-item-team col-sm-4 col-md-3" data-title="<?= $item->title ?>" data-desc="<?= $item->description ?>" data-name="<?= $item->name ?>">                    <?= Html::img('/uploads/team/'.$item->img, ['alt' => $item->name ,'style' => 'border-radius: ' . $radius . '%; width: ' . $width. ';margin: 0 auto;']) ?>                    <?php $sax = $lang_id == 'ka-GE'?$item->name_ge:$item->name ?>                    <?php $teamName = str_replace(" ", " <br> ", $sax); ?>                    <h2 style="color: #FFFFFF !important; font-family:<?=$font_family?> ; font-size: <?= $font_size?>; line-height: 105%"><?= $teamName ?></h2>                    <div style="height: 80px; overflow: hidden !important;">                    <p class="teamdesc"><?= $lang_id == 'ka-GE'?$item->title_ge:$item->title ?></p>                    </div>                </div>            <?php endforeach; ?>        </div>    </div>    <div class="large-5 five columns ds-main-container-team ds-main-back-img-team  hidden-sm" data-image-src="" style="background-color: <?= $background ?>; position: relative;">        <div style="position: absolute; top: 50%; left: 50%; transform: translate(-50%, -50%);width: 100%;padding: 20px 60px;">            <?php //Html::img('', ['alt' => '', 'class' => 'ds-main-img-team', 'style' => 'border-radius: ' . $radius . '%']) ?>            <h1 class="ds-main-name-team uppercase" style="font-family:<?=$font_family?>"><?= $title?></h1>            <div class="ds-main-desc-team"><?= $description ?></div>        </div>    </div></div>